public class ComposeView extends Popover {
    ...
    public ComposeView(boolean smartphone) {
        setClosable(false);

        ...

        // In phones use all space, in tablets just vertically
        if (smartphone) {
            setSizeFull();
        } else {
            setHeight("100%");
            center();
        }
    }

    @Override
    public void attach() {
        super.attach();

        // If tablet is wide, leave some space horizontally
        if (!smartphone) {
            if (getParent().getWidth() > 800) {
                setWidth("80%");
            } else {
                setWidth("100%");
            }
        }
    }
    ...
}

